
import React, { useRef, useState } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import { FiArrowUp } from 'react-icons/fi';
import { Link } from 'react-router-dom';

const BusinessScroll = () => {
  const items = [
    { name: "Infor", description: "Infor is one of ERP", image: "data:image/jpeg;base64," },
    { name: "Odoo", description: "Odoo is one of ERP", image: "data:image/jpeg;base64," },
    { name: "Odoo", description: "Odoo is one of ERP", image: "data:image/jpeg;base64," },
    { name: "SAP", description: "SAP is one of ERP", image: "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcSto33oPYJsUWaPW4ZlFNhtkYkhV5nnSs6sAg&s" },
    { name: "Microsoft Dynamics", description: "Microsoft Dynamics is one of ERP", image: "data:image/jpeg;base64," },
  ];

  // Set the default selected item index (e.g., 0 for the first item)
  const [selectedItemIndex, setSelectedItemIndex] = useState(0);
  const itemRefs = useRef([]);

  const handleClick = (index) => {
    setSelectedItemIndex(index);
    if (itemRefs.current[index]) {
      itemRefs.current[index].scrollIntoView({ behavior: 'smooth', block: 'center' });
    }
  };

  return (
    <div className="relative">
     

      <div className="flex justify-center">
        <AnimatePresence>
          <motion.div
            ref={el => itemRefs.current[selectedItemIndex] = el} // Store ref for each card
            className="flex flex-col items-center w-[60vw] h-[300px] bg-white shadow-lg rounded-lg overflow-hidden transition-transform duration-300 ease-in-out"
            initial={{ opacity: 0 }}
            animate={{ opacity: 1 }}
            exit={{ opacity: 0 }}
          >
            <img src={items[selectedItemIndex].image} alt={items[selectedItemIndex].name} className="w-full h-48 object-cover rounded-t-lg" />
            <div className="p-4">
              <h3 className="text-xl font-semibold">{items[selectedItemIndex].name}</h3>
              <p>{items[selectedItemIndex].description}</p>
              <motion.button
                // onClick={() => setSelectedItemIndex(null)}
                whileHover={{ scale: 1.1 }}
                className="mt-4 text-blue-500 flex items-center"
              >
                {/* <FiArrowUp className="mr-2" /> */}
              <Link  target='_blank'>Now More</Link>
              </motion.button>
            </div>
          </motion.div>
        </AnimatePresence>
          </div>
          
           <div className="flex flex-wrap justify-center mt-8 mb-8">
        {items.map((item, index) => (
          <motion.button
            key={index}
            onClick={() => handleClick(index)}
            whileHover={{ scale: 1.05 }}
            className="m-4 p-2 bg-gray-200 rounded shadow-lg transition-transform duration-300 ease-in-out"
          >
            {item.name}
          </motion.button>
        ))}
      </div>
    </div>
  );
};

export default BusinessScroll;